//initial State
const initial = {
  counter: 0,
  showCounter: true,
};

const reducer = (state = initial, action) => {
  switch (action.type) {
    case "increment":
      return {
        counter: state.counter + 1,
        ...state,
      };
    case "increase":
      return {
        ...state,
        counter: state.counter + action.amout,
      };

    case "decrement":
      return {
        counter: state.counter - 1,
      };
    default:
      return state;
  }
};
export default reducer;
