//initial State
const initial = {
  counter: 0,
};
const reducer = (state = initial, action) => {
  switch (action.type) {
    case "increment":
      return {
        counter: state.counter + 1,
      };

    case "decrement":
      return {
        counter: state.counter - 1,
      };
    default:
      return state;
  }
};
export default reducer;
